sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	if(newValue=='create'){
  g_form.setSectionDisplay('create_anewdistributionlist',true);
	}
		if(newValue!='create'){
  g_form.setSectionDisplay('create_anewdistributionlist',false);
	}
		if(newValue=='modify'){
  g_form.setSectionDisplay('modify_anexistingdistributionlist',true);
	}
		if(newValue!='modify'){
  g_form.setSectionDisplay('modify_anexistingdistributionlist',false);
	}
		if(newValue=='delete'){
  g_form.setSectionDisplay('delete_anexistingdistributionlist',true);
	}
		if(newValue!='delete'){
  g_form.setSectionDisplay('delete_anexistingdistributionlist',false);
	}

   //Type appropriate comment here, and begin script below

}
